
public class FruitClass {
	
		private String name;
		private String colour;
	    
			
	public FruitClass(String name, String colour) {
			// TODO Auto-generated constructor stub
		this.name=name;
		this.colour=colour;
		}
	public String getName()
		{
		return name;
	}
	public String getColour()
		{
		return colour;
	}
	public String toString() {
		return "FruitClass [name=" + name + ", colour =" + colour +"]";
	}

}



